"""# The Flagged texts"""

def append_flag_to_master(flag_data):

    master = FLAG_DIR / "combined_flags.csv"
    master.parent.mkdir(parents=True, exist_ok=True)  # ensure folder exists

    file_exists = master.exists()
    with master.open("a", newline="", encoding="utf-8") as f:
        writer = csv.DictWriter(f, fieldnames=["text", "result", "flagging"])
        if not file_exists:
            writer.writeheader()
        writer.writerow({
            "text":     flag_data.get("data", {}).get("text", ""),
            "result":   flag_data.get("result", ""),
            "flagging": flag_data.get("flagging", "")
        })

def print_combined_flags():

    flag_file = FLAG_DIR / "combined_flags.csv"
    if not flag_file.exists():
        print("No flags recorded yet.")
        return

    with flag_file.open("r", encoding="utf-8", newline="") as f:
        reader = csv.DictReader(f)
        for i, row in enumerate(reader, start=1):
            print(f"Flag #{i}:")
            print(f"  Text:      {row.get('text', '')}")
            print(f"  Result:    {row.get('result', '')}")
            print(f"  Reason:    {row.get('flagging', '')}")
            print()

# Call it:
print_combined_flags()

"""**By clicking “Flag”:**

- The input text, model’s highlighted entities, flag reason , plus a timestamp, are saved to combined_flags.csv

- These human-verified error examples are then merged into your training data to re-fine-tune and improve the NER model
"""
